import {useState} from 'react';
import Container from "../UI/Container";
import Button from "../UI/Button";
import styles from "./AddUser.module.css";

const AddUser = ()=>{


    const [enteredUserName,setUserName] = useState('');
    const [enteredAge,setAge] = useState(0);
    const [my_new_user,setNewUser] = useState([]);

    let userDetails = {};
    const addUserHandler = (e)=>{
        e.preventDefault();

        if(enteredUserName.trim().length === 0 || enteredAge < 1){
            alert("Wrong details");
            return;
        }
        userDetails.username = enteredUserName;
        userDetails.enteredAge = enteredAge;
        setNewUser(prev=>{

            return [userDetails,...prev];   
        });

        setUserName('');
        setAge(0);
        console.log(enteredAge);
        console.log(my_new_user);
    }
    const addUserName = (e)=>{
        setUserName(e.target.value);
    }
    const addAge = (e)=>{
        setAge(e.target.value);
    }


    return(
        <Container className={styles.input}>
        <form onSubmit={addUserHandler}>
            <label htmlFor="username"></label>
            <input type="text" value={enteredUserName} id="username" onChange={addUserName}/>
            <label htmlFor="age">Age(Years)</label>
            <input type="number" id="age" value={enteredAge} onChange={addAge}/>
            <Button type="submit">Add User</Button>
        </form>
        </Container>

    );
}
export default AddUser;
